mapper.select:
SELECT * 
FROM user
WHERE user_id = #{user_id} 
AND user_pass = #{user_pass}

SELECT * 
FROM user_info
WHERE user_name = #{user_name}
AND email = #{email}

SELECT *
FROM user join user_info
ON user.user_no = user_info.user_no
WHERE user_id = #{user_id}
AND email = #{email}

SELECT user_id
FROM user
WHERE user_id = #{id}

	SELECT email
	FROM user_info
	WHERE email = #{id}
